import React, { useState } from "react";
import { useSelector, useDispatch } from "react-redux";
import {
  selectSignedIn,
  selectUserData,
  setInput,
} from "../features/userSlice";
import Avatar from "@material-ui/core/Avatar";
import { GoogleLogout } from "react-google-login";

const Navbar = () => {
  const isSignedIn = useSelector(selectSignedIn);
  const userData = useSelector(selectUserData);
  const [inputVal, setInputVal] = useState("");

  // Button Submit Handler
  const submit = () => {};

  const dispatch = useDispatch();
  // Logout Func
  const logout = () => {
    dispatch(selectSignedIn(false));
    dispatch(selectUserData(null));
  };
  return (
    <div className="navbar">
      <h1 className="navbar__header">BlogMania</h1>
      {isSignedIn && (
        <div className="blog__search">
          <input
            type="text"
            placeholder="Search a Blog"
            value={inputVal}
            onChange={(e) => {
              setInputVal(e.target.value);
            }}
          />
          <button className="submit" onClick={submit}>
            Search
          </button>
        </div>
      )}
      {!isSignedIn ? (
        <div className="navbar__userData">
          <Avatar alt="Remy Sharp" src="/static/images/avatar/1.jpg" />
        </div>
      ) : (
        <div className="navbar__userData">
          <Avatar alt={userData?.name} src={userData?.imageUrl} />
          <h1 className="signedIn"> {userData?.givenName}</h1>
          <GoogleLogout
            clientId="907245300679-hl4ehsh1vud55e7c4b0vpjkhbgf7e69s.apps.googleusercontent.com"
            render={(renderProps) => (
              <button
                onClick={renderProps.onClick}
                disabled={renderProps.disabled}
                className="login__button"
              >
                Logout ðŸ˜®
              </button>
            )}
            onLogoutSuccess={logout}
          />
        </div>
      )}
    </div>
  );
};

export default Navbar;
